import { getAllPosts } from "../lib/api";

export default function Allposts({ allPosts }) {
  return (
    <main>
      <Header />
      <ul>
        {allPosts.map((post) => {
          return <li>{post.title}</li>;
        })}
      </ul>
    </main>
  );
}

export async function getStaticProps() {
  const allPosts = getAllPosts([
    "title",
    "date",
    "slug",
    "author",
    "coverImage",
    "excerpt",
  ]);

  return {
    props: { allPosts },
  };
}
